$(document).ready(function(){var n=$("#smartwizard").smartWizard({justified:!0,lang:{next:"Tiếp",previous:"Quay lại"},toolbarSettings:{toolbarPosition:"both",toolbarButtonPosition:"center"}});n.on("leaveStep",function(n,t,i){if(i==1){var u=$("#deliveryForm").serializeArray(),r=!0;return u.forEach(n=>{$(`#${n.name}`).parent().removeClass("has-error");switch(n.name){case"customerName":n.value==""&&($(`#${n.name}`).parent().addClass("has-error"),r=!1);break;case"customerPhone":n.value==""&&($(`#${n.name}`).parent().addClass("has-error"),r=!1);break;case"deliveryAddress":n.value==""&&($(`#${n.name}`).parent().addClass("has-error"),r=!1)}}),r||Swal.fire("Vui lòng điền đầy đủ thông tin!","Chúng tôi cần đầy đủ thông tin để có thể phục vụ quý khách một cách tốt nhất!","warning"),r}})});